import React, { useState } from "react";

export default function HomePage() {
  const [syllabus, setSyllabus] = useState("");
  const [days, setDays] = useState("");

  return (
    <div className="max-w-3xl mx-auto bg-white shadow-lg rounded-xl p-8 mt-6">

      <h2 className="text-3xl font-semibold text-purple-700 mb-6">
        Create Your Study Plan ðŸ“˜âœ¨
      </h2>

      {/* Syllabus Input */}
      <label className="block mb-2 font-medium">Enter Your Syllabus</label>
      <textarea
        className="w-full border rounded-lg p-3 h-40 focus:ring-2 focus:ring-purple-500 outline-none"
        placeholder="Paste your syllabus here..."
        value={syllabus}
        onChange={(e) => setSyllabus(e.target.value)}
      ></textarea>

      {/* Days */}
      <label className="block mt-6 mb-2 font-medium">How Many Days?</label>
      <input
        type="number"
        className="w-full border rounded-lg p-3 focus:ring-2 focus:ring-purple-500 outline-none"
        placeholder="Enter number of days"
        value={days}
        onChange={(e) => setDays(e.target.value)}
      />

      {/* Submit Button */}
      <button className="mt-6 w-full bg-purple-700 text-white py-3 rounded-lg text-lg font-medium hover:bg-purple-800 transition">
        Generate Plan
      </button>
    </div>
  );
}
