import React, { useEffect, useState } from "react";
import Placard from "../components/Placard";
import { getSavedPlans } from "../api/planApi";

const SavedPlansPage = () => {
  const [savedPlans, setSavedPlans] = useState([]);

  useEffect(() => {
    const fetchPlans = async () => {
      const data = await getSavedPlans();
      setSavedPlans(data);
    };
    fetchPlans();
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Saved Plans</h1>
      {savedPlans.length === 0 ? (
        <p>No saved plans yet.</p>
      ) : (
        savedPlans.map((plan, i) => (
          <div key={i} className="mb-6">
            <h2 className="text-xl font-semibold mb-2">{plan.name || `Plan ${i+1}`}</h2>
            {plan.days.map((p, idx) => (
              <Placard key={idx} day={p.day} topics={p.topics} />
            ))}
          </div>
        ))
      )}
    </div>
  );
};

export default SavedPlansPage;
