import React, { useState } from "react";
import SyllabusForm from "../components/SyllabusForm";
import RatingInput from "../components/RatingInput";
import Placard from "../components/Placard";
import { generatePlan } from "../api/planApi";

const HomePage = () => {
  const [topics, setTopics] = useState([]); // topics for ratings
  const [ratings, setRatings] = useState({});
  const [plan, setPlan] = useState([]);

  const handleFormSubmit = async (syllabus, days) => {
    // For now, split syllabus by lines to simulate topics
    const parsedTopics = syllabus.split("\n").filter(Boolean);
    setTopics(parsedTopics);

    // Initialize ratings
    const initialRatings = {};
    parsedTopics.forEach((t) => (initialRatings[t] = 3));
    setRatings(initialRatings);

    // After rating step, you can call API
  };

  const handleRatingChange = (topic, value) => {
    setRatings({ ...ratings, [topic]: parseInt(value) });
  };

  const handleGeneratePlan = async () => {
    // Mock: call API to generate plan
    const days = 5; // For now, hardcoded
    const result = await generatePlan(topics.join("\n"), days, ratings);
    setPlan(result);
  };

  return (
    <div className="container mx-auto p-4">
      {!topics.length ? (
        <SyllabusForm onSubmit={handleFormSubmit} />
      ) : (
        <div className="mb-4">
          <h2 className="text-xl font-bold mb-2">Rate Your Understanding</h2>
          {topics.map((t) => (
            <RatingInput
              key={t}
              topic={t}
              value={ratings[t]}
              onChange={handleRatingChange}
            />
          ))}
          <button
            className="mt-2 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
            onClick={handleGeneratePlan}
          >
            Generate Study Plan
          </button>
        </div>
      )}

      <div className="mt-6">
        {plan.map((p, i) => (
          <Placard key={i} day={p.day} topics={p.topics} />
        ))}
      </div>
    </div>
  );
};

export default HomePage;
